<span class="line"><span style="color:#F07178;">        </span><span style="color:#A6ACCD;">prevNode</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">=</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">curNode</span></span>
